import React, { useContext } from 'react'
import axios from 'axios'
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css'
import Contexto from '../Contexto/Contexto';


function Login() {

  const [data, setData] = useState([]);
  const {usuario} = useContext(Contexto);
  const {contraseña} = useContext(Contexto);
  const {user} = useContext(Contexto);
  const {pass} =useContext(Contexto);
  const navigate = useNavigate();

  // Aqui habra que comprobar que el usuario y contraseña son correctos
  const handleClick = (e) => {
    if(usuario == data[0].usuario && contraseña == data[0].contraseña){
      navigate('/')
    }
    else {
      alert("Vuelva a intentarlo")
    }
  }

  useEffect(() => {
    // Realizar la petición GET cuando el componente se monte
    axios.get('http://localhost:8080/api/usuarios')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);


  return (
    <div className='imagen'>
      <div className='contenedorLogin'>
        <div className='login'>
          <input onChange={(e) => user(e.target.value)} className='botonesLogin usuario' placeholder='Usuario'/>
          <input onChange={(e) => pass(e.target.value)} className='botonesLogin usuario' placeholder='Cotraseña'/>
          <button onClick={handleClick} className='botonesLogin entrarLogin'>Entrar</button>
        </div>
      </div>
    </div>
  )
}

export default Login
