import React from 'react';
import Inicio from './Plantilla';
import './../App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

export default function Tienda() {
  const imagenes = require.context('./../assets', true);
  const [data, setData] = useState([]);
  const [nombre, setNombre] = useState();
  const [imagen, setImagen] = useState();
  const [precio, setPrecio] = useState();
  const [descripcion, setDescripcion] = useState();
  // const [valor1, setValor1] = ("");
  useEffect(() => {
    // Realizar la petici贸n GET cuando el componente se monte
    axios.get('http://localhost:8080/api/prods')
      .then(response => {
        setData(response.data);
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);
  const Categorias = (e) => {
    useEffect(() => {
      // Realizar la petici贸n GET cuando el componente se monte
      axios.get(`http://localhost:8080/api/prodCategory/:${e.target.value}`)
        .then(response => {
          setData(response.data);
        })
        
        .catch(error => {
          console.error('Error al obtener los datos:', error);
        });
    }, []);
  }
  

  return (
    <Inicio>
      
      <div className="container">
        <input className='buscador' placeholder='productos'/>
        <div>
          <h3>Categorias</h3>
          Donde hay que poner la funcion para que le mande el valor
          <select onChange={Categorias}>
            <option value="1">Mangas</option>
            <option value="2">Comincs</option>
            <option value="3">Figuras</option>
            <option value="4">Juegos</option>
          </select>
        </div>
        
        <div className="cajas">
          {data.map((producto, i) => (
            // Un hover en la imagen que te muestre la descripcion
            <div key={i} className='caja'>
              <span>{producto.nombre}</span>
              <img className='imagenProduc' src={imagenes('./'+ producto.imagen +'.jpg')}/>
              <span>{producto.precio}</span>
            </div>
          )) }
        </div>
      </div>
    </Inicio>
  )
}
