import React from 'react';
import Inicio from './Plantilla';
import './../App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

export default function Tienda() {
  const imagenes = require.context('./../assets', true);
  const [data, setData] = useState([]);
  const [valueCategoria, setValueCategoria] = useState();

  useEffect(() => {
    // Realizar la petición GET cuando el componente se monte
    axios.get('http://localhost:8080/api/prods')
      .then(response => {
        setData(response.data);
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);


  // Busqueda por categoria

  const handleSelectChange = (e) => {
    const nuevaCategoria = e.target.value
    setValueCategoria(nuevaCategoria)
    axios.post(`http://localhost:8080/api/prodCategory/${nuevaCategoria}`)
      .then(response => {
        setData(response.data);
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }


  // Busqueda por letra
  
  const handleInput = (e) => {
    const letra = e.target.value
    axios.post(`http://localhost:8080/api/prodCategory/${letra}`)
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }

  return (
    <Inicio>
      
      <div className="container">
        <input onChange={handleInput} className='buscador' placeholder='Buscar productos'/>
        <div>
          <h3>Categorias</h3>
          <select  onChange={handleSelectChange}>
            {/* Intentar que el valor por defecto sean todos */}
            <option value="" disabled selected>Selecciona una categoría</option>
            <option value="1">Mangas</option>
            <option value="2">Comincs</option>
            <option value="3">Figuras</option>
            <option value="4">Juegos</option>
          </select>
        </div>
        {/* {    console.log(valueCategoria)} */}
        
        <div className="cajas">
          {data.map((producto, i) => (
            // Un hover en la imagen que te muestre la descripcion
            <div key={i} className='caja'>
              <span>{producto.nombre}</span>
              <img className='imagenProduc' src={imagenes('./'+ producto.imagen +'.jpg')} alt={producto.nombre}/>
              <span>{producto.precio}</span>
            </div>
          )) }
        </div>
      </div>
    </Inicio>
  )
}
