import React, {useContext, useEffect, useState} from 'react';
import '../App.css';
import Inicio from './Plantilla';
import Contexto from '../Contexto/Contexto';
import axios from 'axios';

function Carrito() {

  const {pedido} = useContext(Contexto);
  const [data, setData] = useState([]);
  const imagenes = require.context('./../assets', true);

  useEffect(() => {
    axios.post(`http://localhost:8080/api/prodId`, pedido)
      .then(response => {
        setData(response.data);
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);
  
  return (
    <Inicio>

      <div className="cajas container">
        <h2>Pedido</h2>
        {data.map((producto, i) => (
          <div key={i} className='caja'>
            <img className='imagenProduc' src={imagenes('./'+ producto.imagen +'.jpg')} alt={producto.nombre}/>
            <div className='infoProducto'>
              <span className='nombreProducto'>{producto.nombre}</span>
              <span className='precioProducto'>{producto.precio}â‚¬</span>
            </div>
          </div>      
        )) }
      </div>
    </Inicio>
  )
}

export default Carrito
