import React, {useContext, useEffect, useState} from 'react';
import '../App.css';
import Contexto from '../Contexto/Contexto';
import axios from 'axios';

function Carrito() {

  const {pedido} = useContext(Contexto);
  const pedidoString = pedido.join(',');
  const pedidoEnteros = pedido.map(id => parseInt(id, 10));
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.post(`http://localhost:8080/api/prodId/${pedidoString}`)
      .then(response => {
        setData(response.data);
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, [pedidoString]);

  const pulsar = () => {
    console.log(data)
    console.log(pedidoString)
  }

  
  return (
    <div>
      <button onClick={pulsar}>aqui</button>
    </div>
  )
}

export default Carrito
