import React from 'react'
import { useContext, useState, useEffect } from 'react'
import Contexto from '../Contexto/Contexto'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Pedidos() {

  const {usuario} = useContext(Contexto);
  const imagenes = require.context('./../assets', true);
  const [dataPedidos, setDataPedidos] = useState()
  const navigate = useNavigate();
  let pedidos = [];


  useEffect(() => {
    axios.post(`http://localhost:8080/api/encontrarPed/${usuario.id}`)
      .then(response => {
        for(var i = 0; i < response.data.length; i++){
          pedidos.push(JSON.parse(response.data[i].pedido));
        }
        setDataPedidos(pedidos)
      })
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, [usuario.id]);


  const volver = () => {
    navigate('/');
  }

  const fecha = () => {
    var fechaString = "2023-09-20T02:51:07.000Z"; // Tu fecha específica

// Convierte la cadena en un objeto Date
var fecha = new Date(fechaString);

// Obtiene el día, mes y año
var dia = fecha.getDate();
var mes = fecha.getMonth() + 1; // Sumamos 1 porque los meses van de 0 a 11
var año = fecha.getFullYear();

// Puedes mostrar los valores como desees, por ejemplo:
console.log("Día: " + dia);
console.log("Mes: " + mes);
console.log("Año: " + año);
  }


  return (
    <div>
      {dataPedidos !== undefined 
      ?
        dataPedidos.map((pedidoFull, i) => (
          <div key={i}>
            <span>Pedido {i + 1}</span>
            {pedidoFull.map((producto, k) => (
              <div key={k} className=''>
              <img className='imagenProduc' src={imagenes('./'+ producto.imagen +'.jpg')} alt={producto.nombre}/>
              <div className='infoProducto'>
                <span className='nombreProducto'>{producto.nombre}</span>
                <div>
                  <span className='precioProducto'>{producto.precio}€</span>
                  <span>{producto.createdAt}</span>
                </div>
              </div>
            </div>
            )) }
          </div>
        ))
      :
        <span>Cargando</span>
        
      }
      {console.log(dataPedidos)}
      <button onClick={()=>volver()}>Volver</button>
    </div>
  )
}

export default Pedidos
