import React, {useContext, useEffect, useState} from 'react';
import '../App.css';
import Inicio from './Plantilla';
import Contexto from '../Contexto/Contexto';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Carrito() {

  const {pedido, logueado, setPedido} = useContext(Contexto);
  const [data, setData] = useState([]);
  const imagenes = require.context('./../assets', true);
  const navigate = useNavigate();

  
  const borrarProd = (id) => {
    if(pedido.length > 1){
      let tempPedido = [];
      for(var i = 0; i < pedido.length; i++){
        if(pedido[i] !== id){
          tempPedido.push(pedido[i]);
        }
      }
      setPedido(tempPedido);
    }
  } 
    

  useEffect(() => {
    axios.post(`http://localhost:8080/api/prodId`, pedido)
      .then(response => {
        setData(response.data);
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, [borrarProd]);

  
  const comprar = () => {
    if(logueado === true) {
      console.log("comprado");
      console.log(logueado)
    }
    else {
      navigate('/login')
    }
  }

  
  return (
    <Inicio>
      <div className='containerCarro'>

        <div className="cajasCarro">
          <h2>Pedido</h2>
          {data.map((producto, i) => (
            <div key={i} className=''>
              <img className='imagenProduc' src={imagenes('./'+ producto.imagen +'.jpg')} alt={producto.nombre}/>
              <div className='infoProducto'>
                <span className='nombreProducto'>{producto.nombre}</span>
                <div>
                  <span className='precioProducto'>{producto.precio}€</span>
                  <span>Cantidad</span><input type="number" />
                </div>
              </div>
              <button onClick={() => borrarProd(producto.id)}>Eliminar</button>
            </div>
            
          )) }
        </div>

        <div className='detallesCompra'>
          <span className='letra'>Productos: 3</span>
          <span  className='letra'>Total: 24€</span>
          <button onClick={comprar}>Comprar</button>
        </div>
        
      </div>
    </Inicio>
  )
}

export default Carrito
