import React from 'react';
import Inicio from './Plantilla';
import './../App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

export default function Tienda() {
  const [data, setData] = useState([]);
  const url = "./../assets/";
  // const [valor1, setValor1] = ("");
  useEffect(() => {
    // Realizar la petición GET cuando el componente se monte
    axios.get('http://localhost:8080/api/prods')
      .then(response => {
        setData(response.data);
        // setValor1(e.target.value)
      })
      
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);

  return (
    <Inicio>
      
      <div className="container">
        <input className='buscador' placeholder='productos'/>
        <div className="cajas">
          {data.map((producto, i) => (
            // Un hover en la imagen que te muestre la descripcion
            <div key={i} className='caja'>
              <span>{producto.nombre}</span>
              {/* <img src={url + producto.nombre.toLowerCase() + ".jpg"} /> */}
              <img src=''/>
              <span>{producto.precio}</span>
              {console.log(url + producto.nombre.toLowerCase() + ".jpg")}
            </div>
          )) }
  
          
        </div>
        
      </div>
    </Inicio>
  )
}
