import React from 'react';
import axios from 'axios';
import { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';
import Contexto from '../Contexto/Contexto';

function Login() {

  const [data, setData] = useState([]);
  const {usuario, contraseña, user, pass, setLogueado} = useContext(Contexto);
  const navigate = useNavigate();
  const [palabra, setPalabra] = useState("Usuario");

  // Aqui habra que comprobar que el usuario y contraseña son correctos
  const handleClick = (e) => {
    // Realizar la petición GET cuando el componente se monte
    axios.get('http://localhost:8080/api/usuarios')
      .then(response => {
        setData(response.data);
      })
      .then( () => {
        useEffect(() => {
          if(usuario == data[0].usuario && contraseña == data[0].contraseña){
            setLogueado(true);
            navigate('/');
          }
          else {
            alert("Vuelva a intentarlo");
          }
        })
        
      })
      .catch(error => {
        console.error('Error al obtener los datos:', error);
      });
    
  }

  // useEffect(() => {
    
  // }, []);

  // useEffect(() => {
  //   axios.post(url, {
  //     productId:id,
  //     description: comentario
  //   })
  // }, []);

  return (
    <div className='imagen'>
      <div className='contenedorLogin'>
        <div className='login'>
          <span className='spanLogin'>{palabra}</span>
          <input onFocus={() => setPalabra("Usuario")} onChange={(e) => user(e.target.value)} className='botonesLogin usuario'/>
          <input onFocus={() => setPalabra("Contraseña")} onChange={(e) => pass(e.target.value)} className='botonesLogin usuario'/>
          <button onClick={handleClick} className='botonesLogin entrarLogin'>Entrar</button>
        </div>
        <div>
          {/* <button>Nuevo Usuario</button> */}
        </div>
      </div>
    </div>
  )
}

export default Login
