import React from 'react'
import Inicio from './Plantilla';
import './../App.css';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function Eventos() {

  const settings = {
    dots: true,
    rows: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerMode: true
  };

  const imagenes = require.context('./../assets', true);

  return (
    <div>
      <Inicio>
        <h2>Pr√≥ximos eventos</h2>
        <div>
        <Slider {...settings}>
          <div>
            <img className='imgEventos' src={imagenes('./imagenLogin.jpg')} alt="Imagen 1" />
          </div>
          <div>
            <img className='imgEventos' src={imagenes('./japan.png')} alt="Imagen 2" />
          </div>
          <div>
            <img className='imgEventos' src={imagenes('./imagenLogin.jpg')} alt="Imagen 3" />
          </div>
        </Slider>
        </div>
      </Inicio>
    </div>
  )
}

export default Eventos
